function getLocation(){navigator.geolocation?navigator.geolocation.getCurrentPosition(showPosition,handleError):field.innerHTML="Browser does not support GeoLocation"}function handleError(a){switch(a.code){case a.PERMISSION_DENIED:alert("User distrusts app");break;case a.POSITION_UNAVAILABLE:alert("Location information missing.");break;case a.TIMEOUT:alert("Timeout occurred.");break;case a.UNKNOWN_ERROR:alert("Unknown error took place")}alert("Developer error string: "+a.message)}function showPosition(a){field.innerHTML="Latitude: "+a.coords.latitude+"<br>Longitude: "+a.coords.longitude,console.log(a.coords)}var field=document.getElementById("myDataField");getLocation();